import { useState } from 'react';
import './App.css';
import NoteContainer from './components/note_container/NoteContainer';
import Sidebar from './components/sidebar/Sidebar';

function App() {

  const [notes, setNotes] = useState([
    {
      title: "note#",
      text: "abcd",
      time: "3:30AM",
      color: "cyan"
    },
    {
      title: "note#",
      text: "abcd",
      time: "3:30AM",
      color: "yellow"
    },
    {
      title: "note#",
      text: "abcd",
      time: "3:30AM",
      color: "green"
    },
    {
      title: "note#",
      text: "abcd",
      time: "3:30AM",
      color: "pink"
    }
  ]);

  const addNotes = (color) => {

      const tempNotes = [...notes];
      tempNotes.push({
        title: "",
        text: "",
        time: Date.now(),
        color 
      });
      setNotes(tempNotes);
  };

  return (
    <>
      <div className="app_main">
        <Sidebar addNotes={addNotes} />
        <NoteContainer  notes={notes} />
      </div>
    </>
  );
}

export default App;
