import React from 'react'

import './notes.css'

import deleteIcon from '../../assets/delete.png'

export default function Notes(props) {

    const formatDate = (value) => {

        if (!value)
            return "";

        const months = [
            'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'
        ];
        const date = new Date(value);
        let hrs = date.getHours();
        let amPm = hrs ? hrs : "12";
        hrs = hrs>12 ? (hrs =24-hrs) : hrs;
        let min = date.getMinutes();
        let day = date.getDate();

    };


    return (
        <div className='notes' style={{backgroundColor:props.notes.color}}>
            <input className='notes_title' defaultValue={props.notes.title} />
            <textarea className='notes_text' defaultValue={props.notes.text} cols="30" rows="10"></textarea>
            <div className='notes_footer'>
                <p>{props.notes.time}</p>
                <img src={deleteIcon} alt="Delete" onClick={()=>props.deleteNote(props.notes.id)} />
            </div>

        </div>
    )
}
